  <script>
      $(document).ready(function() {
          $('section').each( function(i){
              var bottom_of_object = $(this).offset().top + ($(this).outerHeight()/4);
              var bottom_of_window = $(window).scrollTop() + $(window).height();

              /* If the object is completely visible in the window, fade it it */
              if( bottom_of_window > bottom_of_object ){
                  console.log(  $(this));

                  $(this).animate({'opacity':'1'},500);

              }

          });
          /* Every time the window is scrolled ... */
          $(window).scroll( function(){

              /* Check the location of each desired element */
              $('section').each( function(i){
                  var bottom_of_object = $(this).offset().top + ($(this).outerHeight()/4);
                  var bottom_of_window = $(window).scrollTop() + $(window).height();

                  /* If the object is completely visible in the window, fade it it */
                  if( bottom_of_window > bottom_of_object ){
                      console.log(  $(this));

                      $(this).animate({'opacity':'1'},500);

                  }

              });

          });

      });

          $(".u-active").on("click", ".u-effect-fade.u-gallery-item.u-gallery-item-1", function(){
          window.location = $(this).data("href");
      });

          $('form').submit(function (e){
              var email =  $('input[name="email"]');
              var pattern = /^\b[A-Z0-9._%-]+@[A-Z0-9.-]+\.[A-Z]{2,4}\b$/i ;
              console.log(email.next());

              if(!pattern.test(email.val())){
                  var error =   email.next() ;
                  error.css('display','inline');
                  error.html('votre E-mail n\' est pas valide');
              }else {
                  window.location.reload();
              }
          });

      $('.u-block-1590-6').click(function (e){
          e.preventDefault();
          var ele =   $('.u-active.u-align-center.u-carousel-item') ;
          if (ele.next().length) {
              ele.removeClass('u-active');
              ele.next().addClass('u-active');
          }
      });

      $('.u-block-1590-5').click(function (e){
