<script>

    $('#contactForm').on('submit',function(e) {
        e.preventDefault();
        var test = true ;
        var email =  $('input[name="email"]');
        var telephone =  $('input[name="telephone"]');
        var nom =  $('input[name="nom"]');
        var prenom =  $('input[name="prenom"]');

        var pattern = /^\b[A-Z0-9._%-]+@[A-Z0-9.-]+\.[A-Z]{2,4}\b$/i ;
        var pattern_tel  =/^[0-9]{8}$/ ;

        if(!pattern.test(email.val())) {
            test=false;
            var error =   email.next() ;
            error.css('display','inline');
            error.html('votre E-mail n\' est pas valide');
        }
        if (!pattern_tel.test(telephone.val())){
            test=false;
            var error =   telephone.next() ;
            error.css('display','inline');
            error.html('votre numero n\' est pas valide');
        }
        if ($.isNumeric(nom.val())){
            test=false;
            var error =   nom.next() ;
            error.css('display','inline');
            error.html('vote nom n\' est pas valide');
        }
        if ($.isNumeric(prenom.val())){
            test=false;
            var error =   prenom.next() ;
            error.css('display','inline');
            error.html('vote prenom n\' est pas valide');
        }
        var nb = JSON.parse("{{ json_encode(session('cart') ? count(session('cart')) : 0) }}");
        if(nb==0) {
            console.log($('#errorform'));
            $('#errorform').html('Veulliez ajouter des produits au panier');
            test = false ;
        }
        if (test) {
            $(this).submit(function (res) {
    $('#popup1').css('visibility', 'visible');
    $("#popup1").fadeTo("slow", 1);

            });
        }
    });

</script>
